mapper.select:
SELECT M.*, F.FILE_ORIGIN, F.FILE_CONVERT FROM MOIM M LEFT JOIN FILES F ON M.FILE_ID = F.FILE_ID
M.IS_VISIBLE = 'Y' AND M.IS_ACTIVE = 'Y'
AND M.CATEGORY_ID = #{categoryId}
ORDER BY moim_id DESC LIMIT #{limit} OFFSET #{offset}

SELECT COUNT(*) FROM MOIM
IS_VISIBLE = 'Y' AND IS_ACTIVE = 'Y'
AND CATEGORY_ID = #{categoryId}

SELECT * FROM ( SELECT ROWNUM AS rn, moim_data.* FROM ( SELECT M.*, F.FILE_ORIGIN AS fileOrigin, F.FILE_CONVERT AS fileConvert FROM MOIM M LEFT JOIN FILES F ON M.FILE_ID = F.FILE_ID
M.IS_VISIBLE = 'Y' AND M.IS_ACTIVE = 'Y'
AND M.CATEGORY_ID = #{categoryId}
ORDER BY M.MOIM_ID DESC ) moim_data WHERE ROWNUM <= #{endRow} ) WHERE rn >= #{startRow}

mapper.update:
UPDATE MOIM_ATTENDEE
SET
	USER_ID = #{user_id}
	MOIM_ID = #{moim_id}
	ATT_DATE = SYSDATE
