mapper.select:
/**/
SELECT * FROM (
SELECT ROWNUM AS RNUM, N.*
FROM (
SELECT * FROM NOTICE ORDER BY NOTICE_ID DESC
) N
WHERE ROWNUM <= #{endRow}
)
WHERE RNUM >= #{startRow}
/**/

mapper.update:
update notice
set
notice_title = #{noticeTitle},
notice_content = #{noticeContent}
WHERE NOTICE_ID = #{noticeId}

update notice
set notice_count = #{noticeCount} + 1
WHERE NOTICE_ID = #{noticeId}
